package de.fivethree.immoapps.backend.model.payload.res.checklist;

import java.util.ArrayList;
import java.util.List;

public class ChecklistLevelRep {
    private long id;

    private String name;

    private List<ChecklistPruefkriteriumRep> kriterien = new ArrayList<>();

    public ChecklistLevelRep(long id, String name) {
        this.id = id;
        this.name = name;
    }

    public ChecklistLevelRep(long id, String name, List<ChecklistPruefkriteriumRep> kriterien) {
        this(id, name);
        this.kriterien = kriterien;
    }

    public List<ChecklistPruefkriteriumRep> getKriterien() {
        return kriterien;
    }

    public void setKriterien(List<ChecklistPruefkriteriumRep> kriterien) {
        this.kriterien = kriterien;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }
}
