package de.fivethree.immoapps.backend.model.payload.res.checklist;

import de.fivethree.immoapps.backend.model.payload.res.common.ImageRep;

public class ChecklistPruefkriteriumRep {
    private Long id;

    private String name;

    private ChecklistPruefKriteriumCategoryRep category;

    private ImageRep picto;

    public ChecklistPruefkriteriumRep(Long id, String name, ChecklistPruefKriteriumCategoryRep category, ImageRep picto) {
        this.id = id;
        this.name = name;
        this.category = category;
        this.picto = picto;
    }

    public ImageRep getPicto() {
        return picto;
    }

    public void setPicto(ImageRep picto) {
        this.picto = picto;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public ChecklistPruefKriteriumCategoryRep getCategory() {
        return category;
    }

    public void setCategory(ChecklistPruefKriteriumCategoryRep category) {
        this.category = category;
    }
}
