package de.fivethree.immoapps.backend.model.payload.res.checklist.app;

import java.util.List;

public class ChecklistAppRep {
    private long id;

    private String name;

    private Boolean defaultChecklist;

    private List<ChecklistLevelAppRep> levels;

    public ChecklistAppRep(long id, String name, Boolean defaultChecklist, List<ChecklistLevelAppRep> levels) {
        this.id = id;
        this.name = name;
        this.defaultChecklist = defaultChecklist;
        this.levels = levels;
    }

    public Boolean getDefaultChecklist() {
        return defaultChecklist;
    }

    public void setDefaultChecklist(Boolean defaultChecklist) {
        this.defaultChecklist = defaultChecklist;
    }

    public List<ChecklistLevelAppRep> getLevels() {
        return levels;
    }

    public void setLevels(List<ChecklistLevelAppRep> levels) {
        this.levels = levels;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }
}
