package de.fivethree.immoapps.backend.model.payload.res.property;

import de.fivethree.immoapps.backend.model.payload.res.user.GetUserResponse;

import java.util.List;

public class GetPropertyAndBoundUsersResponse extends GetPropertyResponse {

    private List<GetUserResponse> users;

    public GetPropertyAndBoundUsersResponse(Long id, String name, String city, String zip, String streetAndNo,
                                            List<GetUserResponse> users) {
        super(id, name, city, zip, streetAndNo);
        this.users = users;
    }

    public List<GetUserResponse> getUsers() {
        return users;
    }

    public void setUsers(List<GetUserResponse> users) {
        this.users = users;
    }
}
