package de.fivethree.immoapps.backend.model.payload.res.property;

public class GetPropertyResponse {
    private Long id;

    private String name;

    private String city;

    private String zip;

    private String streetAndNo;

    public GetPropertyResponse(Long id, String name, String city, String zip, String streetAndNo) {
        this.id = id;
        this.name = name;
        this.city = city;
        this.zip = zip;
        this.streetAndNo = streetAndNo;
    }

    public String getAddress() {
        return getStreetAndNo() + ", " + getZip() + " " + getCity();
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getCity() {
        return city;
    }

    public void setCity(String city) {
        this.city = city;
    }

    public String getZip() {
        return zip;
    }

    public void setZip(String zip) {
        this.zip = zip;
    }

    public String getStreetAndNo() {
        return streetAndNo;
    }

    public void setStreetAndNo(String streetAndNo) {
        this.streetAndNo = streetAndNo;
    }
}
