package de.fivethree.immoapps.backend.model.payload.res.property.app;

import de.fivethree.immoapps.backend.model.payload.res.checklist.app.ChecklistAppRep;
import de.fivethree.immoapps.backend.model.payload.res.property.GetPropertyResponse;
import de.fivethree.immoapps.backend.model.payload.res.user.GetUserResponse;

import java.util.List;

public class GetPropertyWithAllDetailsAndItsUsersResponse extends GetPropertyResponse {

    private List<ChecklistAppRep> checklists;

    private List<GetUserResponse> hausmeister;

    public GetPropertyWithAllDetailsAndItsUsersResponse(Long id, String name, String city, String zip, String streetAndNo,
                                                        List<ChecklistAppRep> checklists,
                                                        List<GetUserResponse> users) {
        super(id, name, city, zip, streetAndNo);
        this.checklists = checklists;
        this.hausmeister = users;
    }

    public List<ChecklistAppRep> getChecklists() {
        return checklists;
    }

    public void setChecklists(List<ChecklistAppRep> checklists) {
        this.checklists = checklists;
    }

    public List<GetUserResponse> getHausmeister() {
        return hausmeister;
    }

    public void setHausmeister(List<GetUserResponse> hausmeister) {
        this.hausmeister = hausmeister;
    }
}
