package de.fivethree.immoapps.backend.model.payload.res.property.app;

import de.fivethree.immoapps.backend.model.payload.res.property.GetPropertyResponse;
import de.fivethree.immoapps.backend.model.payload.res.checklist.app.ChecklistAppRep;

import java.util.List;

public class GetPropertyWithAllDetailsResponse extends GetPropertyResponse {

    private List<ChecklistAppRep> checklists;

    public GetPropertyWithAllDetailsResponse(Long id, String name, String city, String zip, String streetAndNo,
                                             List<ChecklistAppRep> checklists) {
        super(id, name, city, zip, streetAndNo);
        this.checklists = checklists;
    }

    public List<ChecklistAppRep> getChecklists() {
        return checklists;
    }

    public void setChecklists(List<ChecklistAppRep> checklists) {
        this.checklists = checklists;
    }
}
