package de.fivethree.immoapps.backend.model.payload.res.task;

import de.fivethree.immoapps.backend.model.payload.res.checklist.ChecklistPruefkriteriumRep;

import java.util.List;

public class TaskRep {
    private Long id;

    private String name;

    private Boolean active;

    private TaskScheduleOptionRep scheduleOption;

    private List<ChecklistPruefkriteriumRep> pruefkriterien;

    private Long propertyId;

    private Long userId;

    private Long auftragGeberId;

    private String startDate;

    private String lastScheduledDate;

    private Boolean valid = true;

    private String notValidReason = null;

    public TaskRep(Long id, String name, Boolean active, TaskScheduleOptionRep scheduleOption,
                   List<ChecklistPruefkriteriumRep> pruefkriterien, Long propertyId, Long userId, Long auftragGeberId,
                   String startDate, String lastScheduledDate, Boolean valid, String notValidReason) {
        this.id = id;
        this.name = name;
        this.active = active;
        this.scheduleOption = scheduleOption;
        this.pruefkriterien = pruefkriterien;
        this.propertyId = propertyId;
        this.userId = userId;
        this.auftragGeberId = auftragGeberId;
        this.startDate = startDate;
        this.lastScheduledDate = lastScheduledDate;
        this.valid = valid;
        this.notValidReason = notValidReason;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Boolean getActive() {
        return active;
    }

    public void setActive(Boolean active) {
        this.active = active;
    }

    public TaskScheduleOptionRep getScheduleOption() {
        return scheduleOption;
    }

    public void setScheduleOption(TaskScheduleOptionRep scheduleOption) {
        this.scheduleOption = scheduleOption;
    }

    public List<ChecklistPruefkriteriumRep> getPruefkriterien() {
        return pruefkriterien;
    }

    public void setPruefkriterien(List<ChecklistPruefkriteriumRep> pruefkriterien) {
        this.pruefkriterien = pruefkriterien;
    }

    public Long getPropertyId() {
        return propertyId;
    }

    public void setPropertyId(Long propertyId) {
        this.propertyId = propertyId;
    }

    public Long getUserId() {
        return userId;
    }

    public void setUserId(Long userId) {
        this.userId = userId;
    }

    public Long getAuftragGeberId() {
        return auftragGeberId;
    }

    public void setAuftragGeberId(Long auftragGeberId) {
        this.auftragGeberId = auftragGeberId;
    }

    public String getStartDate() {
        return startDate;
    }

    public void setStartDate(String startDate) {
        this.startDate = startDate;
    }

    public String getLastScheduledDate() {
        return lastScheduledDate;
    }

    public void setLastScheduledDate(String lastScheduledDate) {
        this.lastScheduledDate = lastScheduledDate;
    }

    public Boolean getValid() {
        return valid;
    }

    public void setValid(Boolean valid) {
        this.valid = valid;
    }

    public String getNotValidReason() {
        return notValidReason;
    }

    public void setNotValidReason(String notValidReason) {
        this.notValidReason = notValidReason;
    }
}
