package de.fivethree.immoapps.backend.model.payload.res.user;

public class GetHausverwalterResponse {
    private Long id;

    private String firstname;

    private String lastname;

    private String email;

    private String role;

    private GetAdditionalUserInfoResponse additionalInfo;

    public GetHausverwalterResponse(Long id, String firstname, String lastname, String email, String role,
                                    GetAdditionalUserInfoResponse additionalInfo) {
        this.id = id;
        this.firstname = firstname;
        this.lastname = lastname;
        this.email = email;
        this.role = role;
        this.additionalInfo = additionalInfo;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getFirstname() {
        return firstname;
    }

    public void setFirstname(String firstname) {
        this.firstname = firstname;
    }

    public String getLastname() {
        return lastname;
    }

    public void setLastname(String lastname) {
        this.lastname = lastname;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public String getRole() {
        return role;
    }

    public void setRole(String role) {
        this.role = role;
    }

    public GetAdditionalUserInfoResponse getAdditionalInfo() {
        return additionalInfo;
    }

    public void setAdditionalInfo(GetAdditionalUserInfoResponse additionalInfo) {
        this.additionalInfo = additionalInfo;
    }
}
