package de.fivethree.immoapps.backend.model.payload.res.user;

public class GetUserResponse {
    private Long id;

    private String firstname;

    private String lastname;

    private String email;

    private String role;

    private Boolean deleted;

    public GetUserResponse(Long id, String firstname, String lastname, String email, String role, Boolean deleted) {
        this.id = id;
        this.firstname = firstname;
        this.lastname = lastname;
        this.email = email;
        this.role = role;

        //in case of null the user isn't deleted!
        if (deleted == null || deleted == false) {
            this.deleted = false;
        } else {
            this.deleted = true;
        }
    }

    public String getRole() {
        return role;
    }

    public void setRole(String role) {
        this.role = role;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getFirstname() {
        return firstname;
    }

    public void setFirstname(String firstname) {
        this.firstname = firstname;
    }

    public String getLastname() {
        return lastname;
    }

    public void setLastname(String lastname) {
        this.lastname = lastname;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public Boolean getDeleted() {
        return deleted;
    }

    public void setDeleted(Boolean deleted) {
        this.deleted = deleted;
    }
}
