package de.fivethree.immoapps.backend.model.payload.res.user;

public class JwtAuthenticationUserResponse {
  private String accessToken;
  private String tokenType = "Bearer";
  private GetUserResponse user;

  public JwtAuthenticationUserResponse(String accessToken, GetUserResponse user) {
    this.accessToken = accessToken;
    this.user = user;
  }

  public GetUserResponse getUser() {
    return user;
  }

  public void setUser(GetUserResponse user) {
    this.user = user;
  }

  public String getAccessToken() {
    return accessToken;
  }

  public void setAccessToken(String accessToken) {
    this.accessToken = accessToken;
  }

  public String getTokenType() {
    return tokenType;
  }

  public void setTokenType(String tokenType) {
    this.tokenType = tokenType;
  }

  public String getFullAuthString() {
    return tokenType + " " + accessToken;
  }
}
