package de.fivethree.immoapps.backend.repository;

import de.fivethree.immoapps.backend.domain.user.PasswordForget;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;

import java.time.LocalDateTime;

public interface PasswordForgetRepository extends JpaRepository<PasswordForget, Long> {
    PasswordForget findByEmail(String email);

    @Query(value = "SELECT p FROM PasswordForget p " +
            "WHERE p.guid = :guid AND p.createdDate >= :minDate")
    PasswordForget findByGuidAndAndCreatedDateHigherThan(@Param("guid") String guid,
                                                         @Param("minDate") LocalDateTime minDate);
}
