package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.checklist.ChecklistPruefkriterium;
import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

@Repository
public interface ChecklistPruefkriteriumRepository extends JpaRepository<ChecklistPruefkriterium, Long> {

    @Query(value = "SELECT p FROM ChecklistPruefkriterium p " +
            "LEFT JOIN FETCH p.level lvl " +
            "LEFT JOIN FETCH p.picto " +
            "LEFT JOIN FETCH p.category " +
            "LEFT JOIN FETCH lvl.checklist chk " +
            "LEFT JOIN FETCH chk.property prop " +
            "LEFT JOIN FETCH prop.hausverwalter hvw " +
            "LEFT JOIN FETCH prop.users us " +
            "WHERE p.id = :id")
    ChecklistPruefkriterium findByIdAndFetchEverything(@Param("id") Long id);

    @Query(value = "SELECT COUNT(c) FROM ChecklistPruefkriterium c " +
            "LEFT JOIN c.picto picto " +
            "WHERE picto = :pictoparam")
    Long countUsageOfPicto(@Param("pictoparam") HausverwalterPicto pictoparam);
}
