package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.checklisttemplate.ChecklistTemplateLevel;
import de.fivethree.immoapps.backend.domain.checklisttemplate.ChecklistTemplatePruefkriterium;
import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

@Repository
public interface ChecklistTemplatePruefkriteriumRepository extends JpaRepository<ChecklistTemplatePruefkriterium, Long> {
    ChecklistTemplatePruefkriterium findByIdAndLevel(Long id, ChecklistTemplateLevel level);

    @Query(value = "SELECT COUNT(c) FROM ChecklistTemplatePruefkriterium c " +
            "LEFT JOIN c.picto picto " +
            "WHERE picto = :picto")
    Long countUsageOfPicto(@Param("picto") HausverwalterPicto picto);
}
