package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.checklisttemplate.ChecklistTemplate;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.Set;

@Repository
public interface ChecklistTemplateRepository extends JpaRepository<ChecklistTemplate, Long> {
    @Query(value = "SELECT c FROM ChecklistTemplate c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto " +
            "WHERE c.id = :id AND c.hausverwalter.id = :hausverwalterId")
    ChecklistTemplate findByIdAndHausverwalter(@Param("id") Long id, @Param("hausverwalterId") Long hausverwalterId);

    @Query(value = "SELECT c FROM ChecklistTemplate c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto " +
            "WHERE c.hausverwalter.id = :hausverwalterId")
    Set<ChecklistTemplate> getAllByHausverwalterAndFetchLevelsAndFetchKriterien(@Param("hausverwalterId") Long hausverwalterId);

    @Query(value = "SELECT c FROM ChecklistTemplate c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto " +
            "WHERE c.hausverwalter.id = :hausverwalterId AND c.defaultChecklist = true")
    Set<ChecklistTemplate> getAllDefaultByHausverwalterAndFetchLevelsAndFetchKriterien(@Param("hausverwalterId") Long hausverwalterId);

    @Query(value = "SELECT c FROM ChecklistTemplate c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto " +
            "WHERE c.id = :id AND c.hausverwalter.id = :hausverwalterId")
    ChecklistTemplate findByIdAndHausverwalterAndFetchLevelsAndFetchKriterien(@Param("id") Long id, @Param("hausverwalterId") Long hausverwalterId);
}
