package de.fivethree.immoapps.backend.repository.inspection;

import de.fivethree.immoapps.backend.domain.inspection.InspectionCriteriaPruefkriterium;
import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

@Repository
public interface InspectionCriteriaPruefkriteriumRepository extends JpaRepository<InspectionCriteriaPruefkriterium, Long> {
    @Query(value = "SELECT COUNT(i) FROM InspectionCriteriaPruefkriterium i " +
            "LEFT JOIN i.picto picto " +
            "WHERE picto = :picto")
    Long countUsageOfPicto(@Param("picto") HausverwalterPicto picto);
}
