package de.fivethree.immoapps.backend.service;

import de.fivethree.immoapps.backend.exceptions.FTLReplacementException;
import freemarker.template.Configuration;
import freemarker.template.Template;
import freemarker.template.TemplateException;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.ui.freemarker.FreeMarkerTemplateUtils;

import java.io.IOException;
import java.util.Map;

@Service
public class FTLReplacementService {
    @Autowired
    private Configuration freemarkerConfig;

    public String getReplacedFTLFile(String ftlFileName, String templateLocation,
                                     Map<String, Object> replacementModel) {
        try {
            freemarkerConfig.setClassForTemplateLoading(this.getClass(), templateLocation);
            freemarkerConfig.setDefaultEncoding("UTF-8");

            Template t = freemarkerConfig.getTemplate(ftlFileName);
            return FreeMarkerTemplateUtils.processTemplateIntoString(t, replacementModel);

        } catch (IOException | TemplateException e) {
            throw new FTLReplacementException("Der Report konnte nicht erstellt werden. Es gab ein Problem beim Bef√ºllen des Templates.");
        }
    }

}
