package de.fivethree.immoapps.backend.service;

import de.fivethree.immoapps.backend.model.mail.SendMailRequest;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.env.Environment;
import org.springframework.http.HttpEntity;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpMethod;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;

import java.util.ArrayList;
import java.util.Collection;
import java.util.Collections;

@Service
public class MailService {
    @Autowired
    private Environment env;

    public void sendHTMLMail(final String receiver, final String subject, final String htmlMessage) {
        this.sendHTMLMail(Collections.singletonList(receiver), new ArrayList<>(), new ArrayList<>(),
                subject, htmlMessage);
    }

    public void sendHTMLMail(final Collection<String> receivers, final Collection<String> ccReceivers,
                             final Collection<String> bccReceivers,
                             final String subject, final String htmlMessage) {
        SendMailRequest request = new SendMailRequest(env.getProperty("spring.mail.username"),
                subject, htmlMessage, receivers, ccReceivers, bccReceivers);

        RestTemplate restTemplate = new RestTemplate();
        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(org.springframework.http.MediaType.APPLICATION_JSON);
        HttpEntity<SendMailRequest> entity = new HttpEntity<>(request, headers);
        restTemplate.exchange(env.getProperty("app.mailservice.url") + "/api/mail/html/send",
                HttpMethod.POST, entity, String.class).getBody();
    }
}
