import { Pipe, PipeTransform } from '@angular/core';
import { Checklist } from 'shared';

@Pipe({
  name: 'checklist'
})
export class ChecklistPipe implements PipeTransform {

  transform(value: Checklist[], args?: any): any {
    if (!value) {
      return 'Keine Checkliste';
    }
    if (value.length == 0) {
      return 'Keine Checkliste';
    } else if (value.length > 1) {
      return value.length + ' Checklisten';
    } else {
      return 'Eine Checkliste';
    }
  }

}
