import { Kriterien } from './../../../../shared/types/types';
import { Pipe, PipeTransform } from '@angular/core';
import { Checklist, Category } from 'shared';

@Pipe({
  name: 'kritcat'
})
export class KritCategoriesFilterPipe implements PipeTransform {

  transform(value: Kriterien[], cat: Category): Kriterien[] {
    console.log('transform', value, cat);
    if (!cat || !value) {
      return value;
    }
    return value.filter(kriterium => kriterium.category.id === cat.id);
  }

}
