import { Pipe, PipeTransform } from '@angular/core';
import { Checklist } from 'shared';

@Pipe({
  name: 'level'
})
export class LevelPipe implements PipeTransform {

  transform(c: Checklist, args?: any): any {
    if (!c) {
      return 'Keine Pr端fbereiche';
    }


    let count = 0;
    if (c.levels) {
      count = count + c.levels.length;
    }

    if (count == 0) {
      return 'Keine Pr端fbereiche';
    } else if (count > 1) {
      return count + ' Pr端fbereiche';
    } else {
      return 'Ein Pr端fbereich';
    }
  }

}
