import { Pipe, PipeTransform } from '@angular/core';
import { Category, Level } from 'shared';

@Pipe({
  name: 'levelcat'
})
export class LevelCategoriesFilterPipe implements PipeTransform {

  transform(value: Level[], cat: Category): Level[] {
    console.log('transform', value, cat);
    if (!cat || !value) {
      return value;
    }
    const ret: Level[] = [];

    return value.
    filter(level => level.kriterien.some(kriterium => kriterium.category.id === cat.id));
  }

}
