import { GetContextResult } from './../../../../shared/types/types';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from '@environments/environment';
import { ChangePasswordPayload } from 'shared';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  constructor(private http: HttpClient) { }


  resetOwnPassword(email: string) {
    return this.http.post<any>(`${environment.baseURL}/api/passwordforget`, { email: email });
  }

  changePassword(payload: ChangePasswordPayload) {
    return this.http.post<any>(`${environment.baseURL}/api/auth/changepassword`, payload);
  }

  getContext() {
    return this.http.get<GetContextResult>(`${environment.baseURL}/appapi/context`);
  }

}
