export interface NotifyOptions {
    animated: boolean;
    message: string;
    cssClass: string | string[];
    color: string;
    duration: number;
    closeButtonText: string;
    showCloseButton: boolean;
    keyboardClose: boolean;
    enterAnimation: any;
    leaveAnimation: any;
    mode: 'ios' | 'md';
    position: 'bottom' | 'middle' | 'top';
    translucent: boolean;
}

export type NotifyType = 'success' | 'hint' | 'warning' | 'error' | 'unknown-error';
