package de.fivethree.immoapps.mail.controller;

import de.fivethree.immoapps.mail.model.SendHTMLMailRequest;
import de.fivethree.immoapps.mail.model.SendPlainMailRequest;
import de.fivethree.immoapps.mail.service.MailService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;

import javax.validation.Valid;
import javax.validation.constraints.NotNull;

@Controller
@RequestMapping("/api/mail")
public class MailController {
    @Autowired
    private MailService mailService;

    @PostMapping("/html/send")
    public ResponseEntity<?> sendHTMLMail(@Valid @NotNull @RequestBody SendHTMLMailRequest sendMailRequest) {
        this.mailService.sendHTMLMail(sendMailRequest.getSender(), sendMailRequest.getReceivers(),
                sendMailRequest.getCcReceivers(), sendMailRequest.getBccReceivers(), sendMailRequest.getSubject(),
                sendMailRequest.getBody());

        return ResponseEntity.ok().build();
    }

    @PostMapping("/plain/send")
    public ResponseEntity<?> sendPlainMail(@Valid @NotNull @RequestBody SendPlainMailRequest sendMailRequest) {
        this.mailService.sendPlainMail(sendMailRequest.getSender(), sendMailRequest.getReceivers(),
                sendMailRequest.getCcReceivers(), sendMailRequest.getBccReceivers(), sendMailRequest.getSubject(),
                sendMailRequest.getBody());
        return ResponseEntity.ok().build();
    }
}
