package de.fivethree.immoapps.mail.jobs;

import de.fivethree.immoapps.mail.domain.Mail;
import de.fivethree.immoapps.mail.repository.MailRepository;
import de.fivethree.immoapps.mail.service.MailSendService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Component;

import java.util.ArrayList;
import java.util.List;
import java.util.Set;

@Component
public class SendMailJob {
    @Autowired
    private MailRepository mailRepository;

    @Autowired
    private MailSendService mailSendService;

    private static final Logger logger = LoggerFactory.getLogger(SendMailJob.class);

    @Scheduled(fixedRate = 5000)
    public void execute() {
        Set<Mail> allMails = mailRepository.findAllAndFetchReceivers();
        List<Mail> mailsToRemove = new ArrayList<>();
        for (Mail mail : allMails) {
            try {
                this.mailSendService.sendMailEntity(mail);
                mailsToRemove.add(mail);
            } catch (Exception e) {
                logger.error(e.getMessage());
            }
        }

        if (mailsToRemove.size() > 0) {
            mailRepository.deleteAll(mailsToRemove);
        }
    }
}
