package de.fivethree.immoapps.mail.repository;

import de.fivethree.immoapps.mail.domain.Mail;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.stereotype.Repository;

import java.util.Set;

@Repository
public interface MailRepository extends JpaRepository<Mail, Long> {
    @Query(value = "SELECT m FROM Mail m " +
            "LEFT JOIN FETCH m.receivers " +
            "LEFT JOIN FETCH m.ccReceivers " +
            "LEFT JOIN FETCH m.bccReceivers ")
    Set<Mail> findAllAndFetchReceivers();
}
