import { Component, OnInit } from '@angular/core';
import { NavParams } from '@ionic/angular';

@Component({
  selector: 'app-property-checklist-actions',
  templateUrl: './property-checklist-actions.component.html',
  styleUrls: ['./property-checklist-actions.component.scss']
})
export class PropertyChecklistActionsComponent implements OnInit {

  constructor(public navParams: NavParams) { }

  ngOnInit() {
  }

  goToChecklist(){
    this.navParams.get('controller').dismiss({},'checklist');
  }

  deleteChecklist(){
    this.navParams.get('controller').dismiss({},'delete');
  }

}
