import { filter } from 'rxjs/operators';
import { Pipe, PipeTransform } from '@angular/core';
import { Checklist } from 'shared';

@Pipe({
  name: 'checklist'
})
export class ChecklistPipe implements PipeTransform {

  transform(names: string[], all: Checklist[]): Checklist[] {
    const ret: Checklist[] = [];
    if (!names || !all) {
      return;
    }
    names.forEach(name => {
      const c = all.find(cs => cs.name === name);
      if (c) {
        ret.push(c);
      }
    });
    return ret;
  }

}
