import { Pipe, PipeTransform } from '@angular/core';
import { User, Property } from 'shared';

@Pipe({
  name: 'propertyByID'
})
export class PropertyByIDPipe implements PipeTransform {

  constructor() {

  }

  transform(id: number, properties: Property[]) {

    return properties.find(property => property.id === id);
  }

}
